html:
{% if doc.quotation_type == "Internal Quotation" %}
    <table class="table table-bordered table-condensed">
        <tr>
            <!--<th style="width:15%;" class="text-center">Item Code</th>-->
            <th style="width:15%;" class="text-center">Item Name</th>
            <th style="width:20%;" class="text-center">Description</th>
            <th style="width:50%;" class="text-center">Image</th>
        </tr>
        {% for i in doc.items %}
            {% if i.wod_no %}
                {% set erf = frappe.db.get_value("Work Order Data", i.wod_no, "equipment_recieved_form") %}
                {% if erf %}
                    {% set erf_doc = frappe.get_doc("Equipment Received Form", erf) %}
                    {% for j in erf_doc.images %}
                        {% if j.item == i.item_name %}
                            <tr>
                                <td class="text-center">{{ j.item }}</td>
                                <!--<td class="text-center">{{ frappe.db.get_value("Item", j.item, "item_name") }}</td>-->
                                <td class="text-center">{% if j.description %}{{ j.description }}{% endif %}</td>
                                <td><img src= {{ j.image }} width="200px" height="200px"> </td>
                            </tr>
                        {% endif %}
                    {% endfor %}
                {% endif %}
            {% endif %}
        {% endfor %}
    </table>
{% else %}
    <p class="text-center"> <strong>Images will be available only for internal quotation</strong></p>
{% endif %}
