css:
footer {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    height: 90px;
    text-align: center;
    padding-top: 10px;
  }

@page {
    margin-bottom: 100px;
  }

html, body, .page-break {
    height: 100%;
  }

body > div.page-break {
    display: block;
    page-break-after: always;
  }

.tab-striped{
    margin: 10px 0 0 0; 
    width: 100%; 
    font-size:12px; 
    border:1px solid black;
}
.tab-striped tbody tr:nth-child(odd){
    background-color: #f2f2f2;
}
